mapper.select:
select id, fid, time, heap_use_mem, no_heap_use_mem, use_mem, create_date, create_user, delete_flag, remark, update_date, update_user from sys_log_monitor_jvm_status where delete_flag = 0
and ${item.name} ${item.symbol} #{item.data}
order by
${item.columnName.name} ${item.symbol.name}

select
id, fid, time, heap_use_mem, no_heap_use_mem, use_mem, create_date, create_user, delete_flag, remark,
update_date, update_user
from sys_log_monitor_jvm_status
where delete_flag = 0


        and ${item.name} ${item.symbol} #{item.data}




    order by

        ${item.columnName.name} ${item.symbol.name}




    limit ${(limit.number-1) * limit.size},${ limit.number * limit.size}

mapper.update:
update sys_log_monitor_jvm_status set
update_date = #{updateDate}
,update_user = #{updateUser}

    ,fid = #{fid}


    ,time = #{time}


    ,heap_use_mem = #{heapUseMem}


    ,no_heap_use_mem = #{noHeapUseMem}


    ,use_mem = #{useMem}


    ,create_date = #{createDate}


    ,create_user = #{createUser}


    ,delete_flag = #{deleteFlag}


    ,remark = #{remark}


    ,update_date = #{updateDate}


    ,update_user = #{updateUser}

where id = #{id}

update sys_log_monitor_jvm_status set
