mapper.select:
select id, create_date, create_user, delete_flag, remark, update_date, update_user, apply_user_id, order_id, order_node_id, monitor_user_id, target_user_id, type, status from sys_order_apply where delete_flag = 0
and ${item.name} ${item.symbol} #{item.data}
order by
${item.columnName.name} ${item.symbol.name}

select
id, create_date, create_user, delete_flag, remark, update_date, update_user, apply_user_id, order_id,
order_node_id, monitor_user_id, target_user_id, type, status
from sys_order_apply
where delete_flag = 0


        and ${item.name} ${item.symbol} #{item.data}




    order by

        ${item.columnName.name} ${item.symbol.name}




    limit ${(limit.number-1) * limit.size},${ limit.number * limit.size}

mapper.update:
update sys_order_apply set
update_date = #{updateDate}
,update_user = #{updateUser}

    ,create_date = #{createDate}


    ,create_user = #{createUser}


    ,delete_flag = #{deleteFlag}


    ,remark = #{remark}


    ,update_date = #{updateDate}


    ,update_user = #{updateUser}


    ,apply_user_id = #{applyUserId}


    ,order_id = #{orderId}


    ,order_node_id = #{orderNodeId}


    ,monitor_user_id = #{monitorUserId}


    ,target_user_id = #{targetUserId}


    ,type = #{type}


    ,status = #{status}

where id = #{id}

update sys_order_apply set
update_date = #{updateDate}
,update_user = #{updateUser}

    ,create_date = #{createDate}


    ,create_user = #{createUser}


    ,delete_flag = #{deleteFlag}


    ,remark = #{remark}


    ,update_date = #{updateDate}


    ,update_user = #{updateUser}


    ,apply_user_id = #{applyUserId}


    ,order_id = #{orderId}


    ,order_node_id = #{orderNodeId}


    ,monitor_user_id = #{monitorUserId}


    ,target_user_id = #{targetUserId}


    ,type = #{type}


    ,status = #{status}

where delete_flag = 0


        and ${item.name} ${item.symbol} #{item.data}

update.foreach:
update sys_order_apply set
update_date = #{item.updateDate}
,update_user = #{item.updateUser}

    ,create_date = #{item.createDate}


    ,create_user = #{item.createUser}


    ,delete_flag = #{item.deleteFlag}


    ,remark = #{item.remark}


    ,update_date = #{item.updateDate}


    ,update_user = #{item.updateUser}


    ,apply_user_id = #{item.applyUserId}


    ,order_id = #{item.orderId}


    ,order_node_id = #{item.orderNodeId}


    ,monitor_user_id = #{item.monitorUserId}


    ,target_user_id = #{item.targetUserId}


    ,type = #{item.type}


    ,status = #{item.status}

where id = #{item.id}

mapper.select:
select count(*)
from sys_order_apply
where delete_flag = 0;
