mapper.select:
select id, create_date, create_user, delete_flag, remark, update_date, update_user, info_id, node_id, deal_user_id, monitor_user_id, result_status, fault_info from sys_order_log where delete_flag = 0
and ${item.name} ${item.symbol} #{item.data}
order by
${item.columnName.name} ${item.symbol.name}

select
id, create_date, create_user, delete_flag, remark, update_date, update_user, info_id, node_id, deal_user_id,
monitor_user_id, result_status, fault_info
from sys_order_log
where delete_flag = 0


        and ${item.name} ${item.symbol} #{item.data}




    order by

        ${item.columnName.name} ${item.symbol.name}




    limit ${(limit.number-1) * limit.size},${ limit.number * limit.size}

mapper.update:
update sys_order_log set
update_date = #{updateDate}
,update_user = #{updateUser}

    ,create_date = #{createDate}


    ,create_user = #{createUser}


    ,delete_flag = #{deleteFlag}


    ,remark = #{remark}


    ,update_date = #{updateDate}


    ,update_user = #{updateUser}


    ,info_id = #{infoId}


    ,node_id = #{nodeId}


    ,deal_user_id = #{dealUserId}


    ,monitor_user_id = #{monitorUserId}


    ,result_status = #{resultStatus}


    ,fault_info = #{faultInfo}

where id = #{id}

