mapper.select:
select id, create_date, create_user, delete_flag, remark, update_date, update_user, base_order_node_id, name, desc, default_value, empty, edit, type, value_type, data_sources, relation_id from sys_order_node_field where delete_flag = 0
and ${item.name} ${item.symbol} #{item.data}
order by
${item.columnName.name} ${item.symbol.name}

select
id, create_date, create_user, delete_flag, remark, update_date, update_user, base_order_node_id, name, desc,
default_value, empty, edit, type, value_type, data_sources, relation_id
from sys_order_node_field
where delete_flag = 0


        and ${item.name} ${item.symbol} #{item.data}




    order by

        ${item.columnName.name} ${item.symbol.name}




    limit ${(limit.number-1) * limit.size},${ limit.number * limit.size}

