mapper.select:
select id, create_date, create_user, delete_flag, remark, update_date, update_user, base_info_id, name, desc, run_deal_user_id, notice_user_id, run_type, status, type, limit_time, init_api_id, run_api_id, save_api_id, trans_api_id, result_type, result_id, sync, suggest from sys_order_node where delete_flag = 0
and ${item.name} ${item.symbol} #{item.data}
order by
${item.columnName.name} ${item.symbol.name}

select
id, create_date, create_user, delete_flag, remark, update_date, update_user, base_info_id, name, desc,
run_deal_user_id, notice_user_id, run_type, status, type, limit_time, init_api_id, run_api_id, save_api_id,
trans_api_id, result_type, result_id, sync, suggest
from sys_order_node
where delete_flag = 0


        and ${item.name} ${item.symbol} #{item.data}




    order by

        ${item.columnName.name} ${item.symbol.name}




    limit ${(limit.number-1) * limit.size},${ limit.number * limit.size}

mapper.update:
