mapper.select:
select id, logger_name, level, trace_id, start_time, log_type, ip, rpc_id, thread_name, project_name, use_time, hash_code, other, create_date, create_user, delete_flag, remark, update_date, update_user from sys_trace_info where delete_flag = 0
and ${item.name} ${item.symbol} #{item.data}
order by
${item.columnName.name} ${item.symbol.name}

select
id, logger_name, level, trace_id, start_time, log_type, ip, rpc_id, thread_name, project_name, use_time,
hash_code, other, create_date, create_user, delete_flag, remark, update_date, update_user
from sys_trace_info
where delete_flag = 0


        and ${item.name} ${item.symbol} #{item.data}




    order by

        ${item.columnName.name} ${item.symbol.name}




    limit ${(limit.number-1) * limit.size},${ limit.number * limit.size}

mapper.update:
update sys_trace_info set
update_date = #{updateDate}
,update_user = #{updateUser}

    ,logger_name = #{loggerName}


    ,level = #{level}


    ,trace_id = #{traceId}


    ,start_time = #{startTime}


    ,log_type = #{logType}


    ,ip = #{ip}


    ,rpc_id = #{rpcId}


    ,thread_name = #{threadName}


    ,project_name = #{projectName}


    ,use_time = #{useTime}


    ,hash_code = #{hashCode}


    ,other = #{other}


    ,create_date = #{createDate}


    ,create_user = #{createUser}


    ,delete_flag = #{deleteFlag}


    ,remark = #{remark}


    ,update_date = #{updateDate}


    ,update_user = #{updateUser}

where id = #{id}

