mapper.select:
select id, logger_name, log_level, trace_id, rpc_id, now_time, log, create_date, create_user, delete_flag, remark, update_date, update_user from sys_trace_log where delete_flag = 0
and ${item.name} ${item.symbol} #{item.data}
order by
${item.columnName.name} ${item.symbol.name}

select
id, logger_name, log_level, trace_id, rpc_id, now_time, log, create_date, create_user, delete_flag, remark,
update_date, update_user
from sys_trace_log
where delete_flag = 0


        and ${item.name} ${item.symbol} #{item.data}




    order by

        ${item.columnName.name} ${item.symbol.name}




    limit ${(limit.number-1) * limit.size},${ limit.number * limit.size}

mapper.update:
update sys_trace_log set
update_date = #{updateDate}
,update_user = #{updateUser}

    ,logger_name = #{loggerName}


    ,log_level = #{logLevel}


