mapper.select:
select id, create_date, create_user, delete_flag, remark, update_date, update_user, name, model_file_path, accuracy, in_param_size, out_param_size, type, structure from sys_algorithm where 1=1
and ${item.name} ${item.symbol} #{item.data}
order by
${item.columnName.name} ${item.symbol.name}

select
id, create_date, create_user, delete_flag, remark, update_date, update_user, name, model_file_path, accuracy,
in_param_size, out_param_size, type, structure
from sys_algorithm
where 1=1


        and ${item.name} ${item.symbol} #{item.data}




    order by

        ${item.columnName.name} ${item.symbol.name}




    limit ${(limit.number-1) * limit.size},${ limit.number * limit.size}

mapper.update:
update sys_algorithm set
update_date = #{updateDate}
,update_user = #{updateUser}

    ,create_date = #{createDate}


    ,create_user = #{createUser}


    ,delete_flag = #{deleteFlag}


    ,remark = #{remark}


    ,update_date = #{updateDate}


    ,update_user = #{updateUser}


    ,name = #{name}


    ,model_file_path = #{modelFilePath}


    ,accuracy = #{accuracy}


    ,in_param_size = #{inParamSize}


    ,out_param_size = #{outParamSize}


    ,type = #{type}


    ,structure = #{structure}

where id = #{id}

update sys_algorithm set
update_date = #{data.updateDate}
,update_user = #{data.updateUser}

    ,create_date = #{data.createDate}


    ,create_user = #{data.createUser}


    ,delete_flag = #{data.deleteFlag}


    ,remark = #{data.remark}


    ,update_date = #{data.updateDate}


    ,update_user = #{data.updateUser}


    ,name = #{data.name}


    ,model_file_path = #{data.modelFilePath}


    ,accuracy = #{data.accuracy}


    ,in_param_size = #{data.inParamSize}


    ,out_param_size = #{data.outParamSize}


    ,type = #{data.type}


    ,structure = #{data.structure}

where delete_flag = 0


        and ${item.name} ${item.symbol} #{item.data}

update.foreach:
update sys_algorithm set
update_date = #{item.updateDate}
,update_user = #{item.updateUser}

    ,create_date = #{item.createDate}


    ,create_user = #{item.createUser}


    ,delete_flag = #{item.deleteFlag}


    ,remark = #{item.remark}


    ,update_date = #{item.updateDate}


    ,update_user = #{item.updateUser}


    ,name = #{item.name}


    ,model_file_path = #{item.modelFilePath}


    ,accuracy = #{item.accuracy}


    ,in_param_size = #{item.inParamSize}


    ,out_param_size = #{item.outParamSize}


    ,type = #{item.type}


    ,structure = #{item.structure}

where id = #{item.id}
