mapper.select:
select id, create_date, create_user, delete_flag, remark, update_date, update_user, app_id, secret_id, secret_key from sys_out_api where 1=1
and ${item.name} ${item.symbol} #{item.data}
order by
${item.columnName.name} ${item.symbol.name}

select
id, create_date, create_user, delete_flag, remark, update_date, update_user, app_id, secret_id, secret_key
from sys_out_api
where 1=1


        and ${item.name} ${item.symbol} #{item.data}




    order by

        ${item.columnName.name} ${item.symbol.name}




    limit ${(limit.number-1) * limit.size},${ limit.number * limit.size}

mapper.update:
update sys_out_api set
update_date = #{updateDate}
,update_user = #{updateUser}

    ,create_date = #{createDate}


    ,create_user = #{createUser}


    ,delete_flag = #{deleteFlag}


    ,remark = #{remark}


    ,update_date = #{updateDate}


    ,update_user = #{updateUser}


    ,app_id = #{appId}


    ,secret_id = #{secretId}


    ,secret_key = #{secretKey}

where id = #{id}

update sys_out_api set
update_date = #{data.updateDate}
,update_user = #{data.updateUser}

    ,create_date = #{data.createDate}


    ,create_user = #{data.createUser}


    ,delete_flag = #{data.deleteFlag}


    ,remark = #{data.remark}


    ,update_date = #{data.updateDate}


    ,update_user = #{data.updateUser}


    ,app_id = #{data.appId}


    ,secret_id = #{data.secretId}


    ,secret_key = #{data.secretKey}

where delete_flag = 0


        and ${item.name} ${item.symbol} #{item.data}

update.foreach:
update sys_out_api set
update_date = #{item.updateDate}
,update_user = #{item.updateUser}

    ,create_date = #{item.createDate}


    ,create_user = #{item.createUser}


    ,delete_flag = #{item.deleteFlag}


    ,remark = #{item.remark}


    ,update_date = #{item.updateDate}


    ,update_user = #{item.updateUser}


    ,app_id = #{item.appId}


    ,secret_id = #{item.secretId}


    ,secret_key = #{item.secretKey}

where id = #{item.id}
