mapper.select:
select id, create_date, create_user, delete_flag, remark, update_date, update_user, ip from my_log.sys_black_list where delete_flag = 0
and ${item.name} ${item.symbol} #{item.data}
limit ${(page-1) * size},${size}

mapper.update:
update my_log.sys_black_list
set update_date = #{updateDate}
, update_user = #{updateUser}

    , create_date = #{createDate}


    , create_user = #{createUser}


    , delete_flag = #{deleteFlag}


    , remark = #{remark}


    , update_date = #{updateDate}


    , update_user = #{updateUser}


    , ip = #{ip}

where id = #{id}

mapper.select:
select count(*)
from my_log.sys_black_list
where delete_flag = 0;

select time
from my_log.sys_log
where ip = #{ip}
and delete_flag = 0

order by time desc
limit #{size}
