mapper.select:
select

from sys_log a
where delete_flag = 0


    and ${item.name} ${item.symbol} #{item.data}

order by time desc

    limit ${(page-1) * size},${size}

mapper.update:
update sys_log set
update_date = #{updateDate}
,update_user = #{updateUser}


    ,exception_detail = #{exceptionDetail}


    ,interface_name = #{interfaceName}


    ,ip = #{ip}


    ,log_type = #{logType}


    ,method_name = #{methodName}


    ,params = #{params}


    ,time = #{time}


    ,user_id = #{userId}


    ,link = #{link}



    ,delete_flag = #{deleteFlag}

where id = #{id}

mapper.select:
select count(*)
from sys_log a
where delete_flag = 0;

select count(*)
from sys_log a
where ${columnName} = #{value}

select count(*)
from sys_log
where time > #{time}
