mapper.select:
select

from sys_log_monitor a
where id = #{id}

select

from sys_log_monitor a
where 1=1

    and ${item.name} ${item.symbol} #{item.data}

order by time desc

    limit ${(page-1) * size},${size}

select count(*)
from sys_log_monitor a
where ${columnName} = #{value}

mapper.update:
update sys_log_monitor a
set a.end_time = #{endTime}
where a.id = #{id}

mapper.select:
select id
from sys_log_monitor
where service_name = #{serviceName}
and time = #{time}
and ip = #{ip}

select

from sys_log_monitor a
where end_time > #{time}

select count(*)
from sys_log_monitor
where service_name = #{serviceName}
and ip = #{ip}
and time = #{time}

mapper.update:
update sys_log_monitor
set end_time = #{now}
where service_name = #{serviceName}
and ip = #{ip}
and end_time > #{now}
