mapper.select:
select

from sys_log_monitor_interface_call a
where 1=1


    and ${item.name} ${item.symbol} #{item.data}

order by time desc

    limit ${(page-1) * size},${size}

select count(*)
from sys_log_monitor_interface_call a
where ${columnName} = #{value}

select count(*)
from sys_log_monitor_interface_call
where time > #{firstStartTile}
