mapper.select:
select
from sys_trace_detail a where delete_flag = 0
and ${item.name} ${item.symbol} #{item.data}

select

from sys_trace_detail a
    where delete_flag = 0


    and ${item.name} ${item.symbol} #{item.data}


    limit ${(page-1) * size},${page * size}

mapper.update:
update sys_trace_detail set

    , trace_id = #{traceId}


    , use_time = #{useTime}


    , end_time = #{endTime}


    , remark = #{remark}


    , type = #{type}


    , update_date = #{updateDate}


    , hash_code = #{hashCode}


    , update_user = #{updateUser}


    , other_two = #{otherTwo}


    , create_user = #{createUser}


    , create_date = #{createDate}


    , delete_flag = #{deleteFlag}


    , other_one = #{otherOne}

where id = #{id}

mapper.select:
select count(*)
from sys_trace_detail a
where delete_flag = 0;

select count(*)
from sys_trace_detail a
where ${columnName} = #{value}
