mapper.select:
select
from sys_trace_info a where delete_flag = 0
and ${item.name} ${item.symbol} #{item.data}

select

from sys_trace_info a
    where delete_flag = 0


    and ${item.name} ${item.symbol} #{item.data}


    limit ${(page-1) * size} , ${page * size}

mapper.update:
update sys_trace_info set

    , trace_id = #{traceId}


    , other = #{other}


    , level = #{level}


    , use_time = #{useTime}


    , remark = #{remark}


    , logger_name = #{loggerName}


    , project_name = #{projectName}


    , update_date = #{updateDate}


    , start_time = #{startTime}


    , log_type = #{logType}


    , update_user = #{updateUser}


    , rpc_id = #{rpcId}


    , hash_code = #{hashCode}


    , thread_name = #{threadName}


    , create_user = #{createUser}


    , create_date = #{createDate}


    , delete_flag = #{deleteFlag}

where id = #{id}

mapper.select:
select count(*)
from sys_trace_info a
where delete_flag = 0;

select count(*)
from sys_trace_info a
where ${columnName} = #{value}

select count(*)
from (
         select count(*)
         from sys_trace_info a
         group by trace_id
         ) b

select count(*)
from sys_trace_info a
where a.log_type = #{code}
  and a.start_time > #{time}
