    ,need_repeat = #{needRepeat}


    ,param = #{param}


    ,result_code = #{resultCode}


    ,type = #{type}


    ,url = #{url}


    ,delete_flag = #{deleteFlag}

where id = #{id}

mapper.select:
select count(*)
from sys_api a
where delete_flag = 0;

