mapper.select:
select
from sys_api_subscribe a where delete_flag = 0
and ${item.name} ${item.symbol} #{item.data}

select

from sys_api_subscribe a
where delete_flag = 0


    and ${item.name} ${item.symbol} #{item.data}


    limit #{(page-1) * size},#{size}

mapper.update:
update sys_api_subscribe set
update_date = #{updateDate}
,update_user = #{updateUser}


    ,api_group_id = #{apiGroupId}


    ,cron = #{cron}


    ,type = #{type}


    ,user_id = #{userId}


    ,delete_flag = #{deleteFlag}

where id = #{id}

mapper.select:
select count(*)
from sys_api_subscribe a
where delete_flag = 0;

select count(*)
from sys_api_subscribe a
where ${columnName} = #{value}

select count(*)
from sys_api_subscribe
where delete_flag = 0
  and user_id = #{userId}
  and api_group_id = #{apiGroupId}
