mapper.select:
select
from sys_job a where delete_flag = 0
and ${item.name} ${item.symbol} #{item.data}

select

from sys_job a
where delete_flag = 0


    and ${item.name} ${item.symbol} #{item.data}


    limit ${(page-1) * size},${size}

mapper.update:
update sys_job set
update_date = #{updateDate}
,update_user = #{updateUser}


    ,method_name = #{methodName}


    ,cron = #{cron}


    ,name = #{name}


    ,params = #{params}


    ,pause = #{pause}


    ,interface_name = #{interfaceName}


    ,param_type = #{paramType}


    ,delete_flag = #{deleteFlag}

where id = #{id}

mapper.select:
select count(*)
from sys_job a
where delete_flag = 0;

select count(*)
from sys_job a
where ${columnName} = #{value}

update sys_job
set pause = 1
where id = #{id}

update sys_job
set pause = 0
where id = #{id}
