mapper.select:
select
from sys_push_page_msg a where delete_flag = 0
and ${item.name} ${item.symbol} #{item.data}

select

from sys_push_page_msg a
where delete_flag = 0


    and ${item.name} ${item.symbol} #{item.data}


    limit ${(page-1) * size},${size}

mapper.update:
update sys_push_page_msg set
update_date = #{updateDate}
,update_user = #{updateUser}



    ,content = #{content}


    ,title = #{title}


    ,user_id = #{userId}


    ,view = #{view}


    ,delete_flag = #{deleteFlag}

where id = #{id}

mapper.select:
select count(*)
from sys_push_page_msg a
where delete_flag = 0;

select count(*)
from sys_push_page_msg a
where ${columnName} = #{value}
