mapper.select:
select id, create_date, create_user, delete_flag, remark, update_date, update_user, device_id, url, meaning from sys_device_callback where delete_flag = 0
and ${item.name} ${item.symbol} #{item.data}
order by
${item.columnName.name} ${item.symbol.name}

select
id, create_date, create_user, delete_flag, remark, update_date, update_user, device_id, url, meaning
from sys_device_callback
where delete_flag = 0


        and ${item.name} ${item.symbol} #{item.data}




    order by

        ${item.columnName.name} ${item.symbol.name}




    limit ${(limit.number-1) * limit.size},${ limit.number * limit.size}

mapper.update:
update sys_device_callback set
update_date = #{updateDate}
,update_user = #{updateUser}

    ,create_date = #{createDate}


    ,create_user = #{createUser}


    ,delete_flag = #{deleteFlag}


    ,remark = #{remark}


    ,update_date = #{updateDate}


    ,update_user = #{updateUser}


    ,device_id = #{deviceId}


    ,url = #{url}


    ,meaning = #{meaning}

where id = #{id}

update sys_device_callback set
update_date = #{updateDate}
,update_user = #{updateUser}

    ,create_date = #{createDate}


    ,create_user = #{createUser}


    ,delete_flag = #{deleteFlag}


    ,remark = #{remark}


    ,update_date = #{updateDate}


    ,update_user = #{updateUser}


    ,device_id = #{deviceId}


    ,url = #{url}


    ,meaning = #{meaning}

where delete_flag = 0


        and ${item.name} ${item.symbol} #{item.data}

update.foreach:
update sys_device_callback set
update_date = #{item.updateDate}
,update_user = #{item.updateUser}

    ,create_date = #{item.createDate}


    ,create_user = #{item.createUser}


    ,delete_flag = #{item.deleteFlag}


    ,remark = #{item.remark}


    ,update_date = #{item.updateDate}


    ,update_user = #{item.updateUser}


    ,device_id = #{item.deviceId}


    ,url = #{item.url}


    ,meaning = #{item.meaning}

where id = #{item.id}

mapper.select:
select count(*)
from sys_device_callback
where delete_flag = 0;
