update.foreach:
update sys_device set
update_date = #{item.updateDate}
,update_user = #{item.updateUser}

    ,create_date = #{item.createDate}


    ,create_user = #{item.createUser}


    ,delete_flag = #{item.deleteFlag}


    ,remark = #{item.remark}


    ,update_date = #{item.updateDate}


    ,update_user = #{item.updateUser}


    ,name = #{item.name}


    ,space_id = #{item.spaceId}


    ,type = #{item.type}


    ,ip = #{item.ip}


    ,port = #{item.port}

where id = #{item.id}

mapper.select:
select count(*)
from sys_device
where delete_flag = 0;
