mapper.select:
select id, create_date, create_user, delete_flag, remark, update_date, update_user, trigger_type, tigger_scene_id, instructions_id, manual from sys_response where delete_flag = 0
and ${item.name} ${item.symbol} #{item.data}
order by
${item.columnName.name} ${item.symbol.name}

select
id, create_date, create_user, delete_flag, remark, update_date, update_user, trigger_type, tigger_scene_id,
instructions_id, manual
from sys_response
where delete_flag = 0


        and ${item.name} ${item.symbol} #{item.data}




    order by

        ${item.columnName.name} ${item.symbol.name}




    limit ${(limit.number-1) * limit.size},${ limit.number * limit.size}

mapper.update:
update sys_response set
update_date = #{updateDate}
,update_user = #{updateUser}

    ,create_date = #{createDate}


    ,create_user = #{createUser}


    ,delete_flag = #{deleteFlag}


    ,remark = #{remark}


    ,update_date = #{updateDate}


    ,update_user = #{updateUser}


    ,trigger_type = #{triggerType}


    ,tigger_scene_id = #{tiggerSceneId}


    ,instructions_id = #{instructionsId}


    ,manual = #{manual}

where id = #{id}

update sys_response set
update_date = #{updateDate}
,update_user = #{updateUser}

    ,create_date = #{createDate}


    ,create_user = #{createUser}


    ,delete_flag = #{deleteFlag}


    ,remark = #{remark}


    ,update_date = #{updateDate}


    ,update_user = #{updateUser}


    ,trigger_type = #{triggerType}


    ,tigger_scene_id = #{tiggerSceneId}


    ,instructions_id = #{instructionsId}


    ,manual = #{manual}

where delete_flag = 0


        and ${item.name} ${item.symbol} #{item.data}

update.foreach:
update sys_response set
update_date = #{item.updateDate}
,update_user = #{item.updateUser}

    ,create_date = #{item.createDate}


    ,create_user = #{item.createUser}


    ,delete_flag = #{item.deleteFlag}


    ,remark = #{item.remark}


    ,update_date = #{item.updateDate}


    ,update_user = #{item.updateUser}


    ,trigger_type = #{item.triggerType}


    ,tigger_scene_id = #{item.tiggerSceneId}


    ,instructions_id = #{item.instructionsId}


    ,manual = #{item.manual}

where id = #{item.id}

mapper.select:
select count(*)
from sys_response
where delete_flag = 0;
