where delete_flag = 0


        and ${item.name} ${item.symbol} #{item.data}

update.foreach:
update sys_space set
update_date = #{item.updateDate}
,update_user = #{item.updateUser}

    ,create_date = #{item.createDate}


    ,create_user = #{item.createUser}


    ,delete_flag = #{item.deleteFlag}


    ,remark = #{item.remark}


    ,update_date = #{item.updateDate}


    ,update_user = #{item.updateUser}


    ,fid = #{item.fid}


    ,points = #{item.points}

where id = #{item.id}

mapper.select:
select count(*)
from sys_space
where delete_flag = 0;
