mapper.select:
select
from sys_server a where delete_flag = 0
and ${item.name} ${item.symbol} #{item.data}

select

from sys_server a
where delete_flag = 0


    and ${item.name} ${item.symbol} #{item.data}


    limit ${(page-1) * size},${size}

mapper.update:
update sys_server set
update_date = #{updateDate}
,update_user = #{updateUser}


    ,ip = #{ip}


    ,name = #{name}


    ,password = #{password}


    ,username = #{username}


    ,type = #{type}


    ,port = #{port}



    ,delete_flag = #{deleteFlag}

where id = #{id}

mapper.select:
select count(*)
from sys_server a
where ${columnName} = #{value}
