mapper.select:
select id, create_date, create_user, delete_flag, remark, update_date, update_user, other_1, other_2, password, port, server_id, start_sh, status, status_sh, stop_sh, type, username, version from sys_software where delete_flag = 0
and ${item.name} ${item.symbol} #{item.data}
order by
${item.columnName.name} ${item.symbol.name}

select
id, create_date, create_user, delete_flag, remark, update_date, update_user, other_1, other_2, password, port,
server_id, start_sh, status, status_sh, stop_sh, type, username, version
from sys_software
where delete_flag = 0


        and ${item.name} ${item.symbol} #{item.data}




    order by

        ${item.columnName.name} ${item.symbol.name}




    limit ${(limit.number-1) * limit.size},${ limit.number * limit.size}

