mapper.select:
select

from good
where is_delete = 0


    and ${item.name} ${item.symbol} #{item.data}


    limit ${(page-1) * size},${page * size}

select
from good where is_delete = 0
and ${item.name} ${item.symbol} #{item.data}

update good
set is_delete = 1
where id = #{id}

select count(*)
from good
where is_delete = 0;

mapper.update:
update good set
update_date = #{updateDate}
,update_user = #{updateUser}

    ,name = #{name}


    ,num = #{num}


    ,type_id = #{typeId}


    ,shop_id = #{shopId}


    ,price = #{price}


    ,is_discount = #{isDiscount}


    ,discount_per = #{discountPer}


    ,user_id = #{userId}


    ,remark = #{remark}

where id = #{id}
