mapper.select:
select

from order
where is_delete = 0


    and ${item.name} ${item.symbol} #{item.data}


    limit ${(page-1) * size},${page * size}

select
from order where is_delete = 0
and ${item.name} ${item.symbol} #{item.data}

update order
set is_delete = 1
where id = #{id}

select count(*)
from order
where is_delete = 0;

mapper.update:
update order set
update_date = #{updateDate}
,update_user = #{updateUser}


    ,status = #{status}


    ,good_name = #{goodName}


    ,push_user_id = #{pushUserId}


    ,push_user_name = #{pushUserName}


    ,push_shop_id = #{pushShopId}


    ,push_shop_name = #{pushShopName}


    ,remark = #{remark}

where id = #{id}
