mapper.select:
select

from shop
where is_delete = 0

    and ${item.name} ${item.symbol} #{item.data}


    limit ${(page-1) * size},${page * size}

select
from shop where is_delete = 0
and ${item.name} ${item.symbol} #{item.data}

update shop
set is_delete = 1
where id = #{id}

select count(*)
from shop
where is_delete = 0;

mapper.update:
update shop set
update_date = #{updateDate}
,update_user = #{updateUser}

    ,user_id = #{userId}


    ,name = #{name}


    ,remark = #{remark}

where id = #{id}
