mapper.select:
select id, create_date, create_user, delete_flag, remark, update_date, update_user, name from sys_dept where delete_flag = 0
and ${item.name} ${item.symbol} #{item.data}
order by
${item.columnName.name} ${item.symbol.name}

select id, create_date, create_user, delete_flag, remark, update_date, update_user, name from sys_dept where delete_flag = 0
and ${item.name} ${item.symbol} #{item.data}
order by
${item.columnName.name} ${item.symbol.name}
limit ${(limit.number-1) * limit.size},${ limit.number * limit.size}

