mapper.select:
select
from sys_dict a where delete_flag = 0
and ${item.name} ${item.symbol} #{item.data}

select

from sys_dict a
where delete_flag = 0


    and ${item.name} ${item.symbol} #{item.data}


    limit ${(page-1) * size},${page * size}

mapper.update:
update sys_dict set
update_date = #{updateDate}
,update_user = #{updateUser}


    ,name = #{name}


    ,code = #{code}


    ,description = #{description}


    ,type = #{type}




    ,delete_flag = #{deleteFlag}

where id = #{id}

mapper.select:
select count(*)
from sys_dict a
where delete_flag = 0;

select count(*)
from sys_dict a
where ${columnName} = #{value}
