mapper.select:
select id, create_date, create_user, delete_flag, remark, update_date, update_user, code, description, name, type from sys_dict where delete_flag = 0
and ${item.name} ${item.symbol} #{item.data}
order by
${item.columnName.name} ${item.symbol.name}

select
id, create_date, create_user, delete_flag, remark, update_date, update_user, code, description, name, type
from sys_dict
where delete_flag = 0


        and ${item.name} ${item.symbol} #{item.data}




    order by

        ${item.columnName.name} ${item.symbol.name}




    limit ${(limit.number-1) * limit.size},${ limit.number * limit.size}

mapper.update:
update sys_dict set
update_date = #{updateDate}
,update_user = #{updateUser}

    ,create_date = #{createDate}


    ,create_user = #{createUser}


    ,delete_flag = #{deleteFlag}


    ,remark = #{remark}


    ,update_date = #{updateDate}


    ,update_user = #{updateUser}


    ,code = #{code}


    ,description = #{description}


    ,name = #{name}


    ,type = #{type}

where id = #{id}

update sys_dict set
update_date = #{data.updateDate}
,update_user = #{data.updateUser}

    ,create_date = #{data.createDate}


    ,create_user = #{data.createUser}


    ,delete_flag = #{data.deleteFlag}


    ,remark = #{data.remark}


    ,update_date = #{data.updateDate}


    ,update_user = #{data.updateUser}


    ,code = #{data.code}


    ,description = #{data.description}


    ,name = #{data.name}


    ,type = #{data.type}

where delete_flag = 0


        and ${item.name} ${item.symbol} #{item.data}

update.foreach:
update sys_dict set
update_date = #{item.updateDate}
,update_user = #{item.updateUser}

    ,create_date = #{item.createDate}


    ,create_user = #{item.createUser}


    ,delete_flag = #{item.deleteFlag}


    ,remark = #{item.remark}


    ,update_date = #{item.updateDate}


    ,update_user = #{item.updateUser}


    ,code = #{item.code}


    ,description = #{item.description}


    ,name = #{item.name}


    ,type = #{item.type}

where id = #{item.id}

mapper.select:
select count(*)
from sys_dict
where delete_flag = 0;

select id
from sys_dict
where code = #{code}
