mapper.select:
select id, create_date, create_user, delete_flag, remark, update_date, update_user, description, dict_id, sort_order, text, value, status from sys_dict_item where delete_flag = 0
and ${item.name} ${item.symbol} #{item.data}
order by
${item.columnName.name} ${item.symbol.name}

select
id, create_date, create_user, delete_flag, remark, update_date, update_user, description, dict_id, sort_order,
text, value, status
from sys_dict_item
where delete_flag = 0


        and ${item.name} ${item.symbol} #{item.data}




    order by

        ${item.columnName.name} ${item.symbol.name}




    limit ${(limit.number-1) * limit.size},${ limit.number * limit.size}

mapper.update:
update sys_dict_item set
update_date = #{updateDate}
,update_user = #{updateUser}

    ,create_date = #{createDate}


    ,create_user = #{createUser}


    ,delete_flag = #{deleteFlag}


    ,remark = #{remark}


    ,update_date = #{updateDate}


    ,update_user = #{updateUser}


    ,description = #{description}


    ,dict_id = #{dictId}


    ,sort_order = #{sortOrder}


    ,text = #{text}


    ,value = #{value}


    ,status = #{status}

where id = #{id}

update sys_dict_item set
update_date = #{updateDate}
,update_user = #{updateUser}

    ,create_date = #{createDate}


    ,create_user = #{createUser}


    ,delete_flag = #{deleteFlag}


    ,remark = #{remark}


    ,update_date = #{updateDate}


    ,update_user = #{updateUser}


    ,description = #{description}


    ,dict_id = #{dictId}


    ,sort_order = #{sortOrder}


    ,text = #{text}


    ,value = #{value}


    ,status = #{status}

where delete_flag = 0


        and ${item.name} ${item.symbol} #{item.data}

update.foreach:
update sys_dict_item set
update_date = #{item.updateDate}
,update_user = #{item.updateUser}

    ,create_date = #{item.createDate}


    ,create_user = #{item.createUser}


    ,delete_flag = #{item.deleteFlag}


    ,remark = #{item.remark}


    ,update_date = #{item.updateDate}


    ,update_user = #{item.updateUser}


    ,description = #{item.description}


    ,dict_id = #{item.dictId}


    ,sort_order = #{item.sortOrder}


    ,text = #{item.text}


    ,value = #{item.value}


    ,status = #{item.status}

where id = #{item.id}

mapper.select:
select count(*)
from sys_dict_item
where delete_flag = 0;

mapper.update:
update sys_dict_item
set delete_flag = 1
where dict_id = #{id}
