mapper.select:
select id, create_date, create_user, delete_flag, remark, update_date, update_user, fid, i_frame, icon, name, sort, target, type, url from sys_menu where delete_flag = 0
and ${item.name} ${item.symbol} #{item.data}
order by
${item.columnName.name} ${item.symbol.name}

select id, create_date, create_user, delete_flag, remark, update_date, update_user, fid, i_frame, icon, name, sort, target, type, url from sys_menu where delete_flag = 0
and ${item.name} ${item.symbol} #{item.data}
order by
${item.columnName.name} ${item.symbol.name}
limit ${(limit.number-1) * limit.size},${ limit.number * limit.size}

mapper.update:
