update.foreach:
update sys_menu set
update_date = #{item.updateDate}
    ,update_user = #{item.updateUser}

    ,create_date = #{item.createDate}


    ,create_user = #{item.createUser}


    ,delete_flag = #{item.deleteFlag}


    ,remark = #{item.remark}


    ,update_date = #{item.updateDate}


    ,update_user = #{item.updateUser}


    ,fid = #{item.fid}


    ,i_frame = #{item.iFrame}


    ,icon = #{item.icon}


    , name = #{item.name}


    ,sort = #{item.sort}


    ,target = #{item.target}


    ,type = #{item.type}


    ,url = #{item.url}

where id = #{item.id}

mapper.select:
select count(*)
from sys_menu
where delete_flag = 0;

mapper.update:
update sys_menu
set delete_flag = 1 where id in

    #{collect}

mapper.delete:
delete
from sys_dept_menu where menu_id in

    #{menuIds}

delete
from sys_dept_menu where dept_id in

    #{deptIds}
