mapper.select:
select
from sys_menu a where delete_flag = 0
and ${item.name} ${item.symbol} #{item.data}

select

from sys_menu a
where delete_flag = 0


    and ${item.name} ${item.symbol} #{item.data}


    limit ${(page-1) * size},${page * size}

mapper.update:
update sys_menu set
update_date = #{updateDate}
,update_user = #{updateUser}

    ,fid = #{fid}


    ,i_frame = #{iFrame}


    ,icon = #{icon}


    ,name = #{name}


    ,sort = #{sort}


    ,type = #{type}


    ,url = #{url}


    ,delete_flag = #{deleteFlag}

where id = #{id}

mapper.select:
select count(*)
from sys_menu a
where delete_flag = 0;

select count(*)
from sys_menu a
where ${columnName} = #{value}
