mapper.select:
select id, create_date, create_user, delete_flag, remark, update_date, update_user, interface_name, method_name, test from sys_power where delete_flag = 0
and ${item.name} ${item.symbol} #{item.data}
order by
${item.columnName.name} ${item.symbol.name}

select id, create_date, create_user, delete_flag, remark, update_date, update_user, interface_name, method_name, test from sys_power where delete_flag = 0
and ${item.name} ${item.symbol} #{item.data}
order by
${item.columnName.name} ${item.symbol.name}
limit ${(limit.number-1) * limit.size},${ limit.number * limit.size}

mapper.update:
update sys_power set update_date = #{updateDate}
                   , update_user = #{updateUser}

    , create_date = #{createDate}


    , create_user = #{createUser}


    , delete_flag = #{deleteFlag}


    , remark = #{remark}


    , update_date = #{updateDate}


    , update_user = #{updateUser}


    , interface_name = #{interfaceName}


    , method_name = #{methodName}


    , test = #{test}

where id = #{id}

