mapper.select:
select

from user
where is_delete = 0


    and ${item.name} ${item.symbol} #{item.data}


    limit ${(page-1) * size},${page * size}

select
from user where is_delete = 0
and ${item.name} ${item.symbol} #{item.data}

update user
set is_delete = 1
where id = #{id}

select count(*)
from user
where is_delete = 0;

mapper.update:
update user set
update_date = #{updateDate}
,update_user = #{updateUser}

    ,nick_name = #{nickName}


    ,user_name = #{userName}


    ,password = #{password}


    ,is_delete = #{isDelete}


    ,remark = #{remark}

where id = #{id}
