mapper.select:
select

from sys_user a
where delete_flag = 0


    and ${item.name} ${item.symbol} #{item.data}


    limit #{(page-1) * size},#{size}

select
from sys_user a where delete_flag = 0
and ${item.name} ${item.symbol} #{item.data}

select count(*)
from sys_user a
where delete_flag = 0;

select count(*)
from sys_user a
where ${columnName} = #{value}

mapper.update:
update sys_user set
update_date = #{updateDate}
,update_user = #{updateUser}

    ,nick_name = #{nickName}


    ,username = #{userName}


    ,password = #{password}


    ,mail = #{mail}


    ,phone = #{phone}


    ,head_portrait = #{headPortrait}


    ,role_id = #{roleId}


    ,delete_flag = #{deleteFlag}


    ,remark = #{remark}

where id = #{id}

mapper.select:
