mapper.select:
select id, create_date, create_user, delete_flag, remark, update_date, update_user, apply_user_id, order_id,target_user_id, order_node_id, monitor_user_id, type, status from my_order.sys_order_apply where delete_flag = 0
and ${item.name} ${item.symbol} #{item.data}
limit ${(page-1) * size},${size}

mapper.update:
update my_order.sys_order_apply
set
update_date = #{updateDate}
,update_user = #{updateUser}

    ,create_date = #{createDate}


    ,create_user = #{createUser}


    ,delete_flag = #{deleteFlag}


    ,remark = #{remark}


    ,apply_user_id = #{applyUserId}


    ,order_id = #{orderId}


    ,order_node_id = #{orderNodeId}


    ,monitor_user_id = #{monitorUserId}


    ,target_user_id = #{targetUserId}


    ,type = #{type}


    ,status = #{status}

where id = #{id}

mapper.select:
select count(*)
from my_order.sys_order_apply
where delete_flag = 0;
