mapper.select:
select
from sys_order_info a where delete_flag = 0
and ${item.name} ${item.symbol} #{item.data}

select

from sys_order_info a
where delete_flag = 0


    and ${item.name} ${item.symbol} #{item.data}


    limit ${(limit.page-1) * limit.size},${limit.size}

