mapper.select:
select
id, create_date, create_user, delete_flag, remark, update_date, update_user, node_field_id, real_value
from my_order.sys_order_node_field_value
where delete_flag = 0


        and ${item.name} ${item.symbol} #{item.data}




    limit ${(page-1) * size},${size}

select count(*)
from my_order.sys_order_node_field_value
where delete_flag = 0;
